template.value:
$(".ajax_submit_product").submit(function (e) {
        e.preventDefault();

        $.ajax({
            type: $(this).attr("method"),
            url: $(this).attr("action"),
            data: new FormData(this), // Data sent to server, a set of key/value pairs (i.e. form fields and values)
            contentType: false,
            cache: false, // To unable request pages to be cached
            processData: false, // To send DOMDocument or non processed data file it is set to false

            beforeSend: function () {
                //$('.panel').waitMe({effect: 'roundBounce'});
            },
            complete: function () {
                //$('.panel').waitMe('hide');
                //$("#loading").hide();
            },
            error: function (result) {
                if (result.status === 401) //redirect if not authenticated user.
                    $(location).prop('pathname', 'auth/login');
                if (result.status == 422) {
                    $('.form-group').each(function () {
                        $(this).removeClass('has-error');
                        $(this).children(".help-block").html("");
                    });

                    var json = result.responseJSON;
                    $.each(json, function (key, value) {
                        $("input[name='" + key + "'],textarea[name='" + key + "'],select[name='" + key + "'] ").parent().addClass("has-error");
                        $("input[name='" + key + "'],textarea[name='" + key + "'],select[name='" + key + "'] ").next(".help-block").html("<strong>" + value[0] + "</strong>");
                    });
                }
            },
            success: function (json) {

                $('.form-group').each(function () {
                    $(this).removeClass('has-error');
                    $(this).children(".help-block").html("");
                });
                if (json.result == "success") {
                    window.location = json.url;
                }
            }
        });
    });

